import React, { useState, useEffect } from "react";
import {
  Box,
  Typography,
  Button,
  Container,
  Paper,
  Grid,
  Card,
  CardContent,
  Chip,
  Avatar,
  LinearProgress,
  TextField,
  Alert,
  Snackbar,
  ToggleButton,
  ToggleButtonGroup,
} from '@mui/material';
import { 
  GitHub, 
  LinkedIn, 
  Email,
  School,
  Work,
  LocationOn,
  Web,
  Storage,
  CloudQueue,
  PhoneAndroid,
  Launch,
  Code,
  Phone,
  Twitter,
  Send,
} from '@mui/icons-material';
import Navbar from "../components/Navbar";

export default function HomePage() {
  const [text, setText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  // Projects filter state
  const [filter, setFilter] = useState('all');

  // Contact form state
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');

  // Smooth scroll function
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({
        behavior: 'smooth',
        block: 'start',
      });
    }
  };

  useEffect(() => {
    const roles = ['Computer Science Student', 'Full Stack Developer', 'Problem Solver', 'Tech Enthusiast'];

    const handleType = () => {
      const current = loopNum % roles.length;
      const fullText = roles[current];

      setText(isDeleting 
        ? fullText.substring(0, text.length - 1)
        : fullText.substring(0, text.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 150);

      if (!isDeleting && text === fullText) {
        setTimeout(() => setIsDeleting(true), 500);
      } else if (isDeleting && text === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [text, isDeleting, loopNum, typingSpeed]);

  const codeLines = [
    { keyword: 'const', variable: ' developer', operator: ' = ', string: '"passionate"', punctuation: ';' },
    { keyword: 'const', variable: ' skills', operator: ' = ', string: '["React", "Node.js", "Python"]', punctuation: ';' },
    { keyword: 'const', variable: ' passion', operator: ' = ', string: '"building amazing things"', punctuation: ';' },
    { keyword: 'function', variable: ' createImpact', operator: '() {', string: '', punctuation: '' },
    { keyword: '  return', variable: ' innovation', operator: ' + ', string: 'dedication', punctuation: ';' },
    { keyword: '}', variable: '', operator: '', string: '', punctuation: '' },
  ];

  // About section data
  const stats = [
    { label: 'Years of Study', value: '3+', icon: <School /> },
    { label: 'Projects Completed', value: '15+', icon: <Work /> },
    { label: 'Technologies Learned', value: '20+', icon: <School /> },
    { label: 'Coffee Cups', value: 'âˆž', icon: <Work /> },
  ];

  const interests = [
    'Web Development', 'Machine Learning', 'Mobile Apps', 'UI/UX Design',
    'Open Source', 'Gaming', 'Music', 'Photography', 'Travel', 'Reading'
  ];

  // Skills section data
  const skillCategories = [
    {
      title: 'Frontend Development',
      icon: <Web />,
      skills: [
        { name: 'React', level: 90 },
        { name: 'JavaScript', level: 85 },
        { name: 'HTML/CSS', level: 95 },
        { name: 'TypeScript', level: 75 },
        { name: 'Material-UI', level: 80 },
      ],
    },
    {
      title: 'Backend Development',
      icon: <Storage />,
      skills: [
        { name: 'Node.js', level: 80 },
        { name: 'Python', level: 85 },
        { name: 'Express.js', level: 75 },
        { name: 'REST APIs', level: 80 },
        { name: 'GraphQL', level: 60 },
      ],
    },
    {
      title: 'Database & Cloud',
      icon: <CloudQueue />,
      skills: [
        { name: 'MongoDB', level: 75 },
        { name: 'PostgreSQL', level: 70 },
        { name: 'Firebase', level: 80 },
        { name: 'AWS', level: 65 },
        { name: 'Docker', level: 60 },
      ],
    },
    {
      title: 'Mobile & Tools',
      icon: <PhoneAndroid />,
      skills: [
        { name: 'React Native', level: 70 },
        { name: 'Git/GitHub', level: 90 },
        { name: 'VS Code', level: 95 },
        { name: 'Figma', level: 75 },
        { name: 'Postman', level: 85 },
      ],
    },
  ];

  const currentlyLearning = [
    'Next.js',
    'Kubernetes',
    'TensorFlow',
    'Go Programming',
  ];

  const learningGoals = [
    'Machine Learning with Python',
    'DevOps and CI/CD',
    'Microservices Architecture',
    'Advanced React Patterns',
    'System Design',
    'Blockchain Development',
  ];

  // Projects data
  const projects = [
    {
      id: 1,
      title: 'E-Commerce Platform',
      description: 'A full-stack e-commerce solution with React frontend and Node.js backend, featuring user authentication, payment integration, and admin dashboard.',
      technologies: ['React', 'Node.js', 'MongoDB', 'Stripe'],
      category: 'fullstack',
      github: 'https://github.com/JaniduP2003/ecommerce-platform',
      demo: 'https://ecommerce-demo.vercel.app',
      featured: true,
    },
    {
      id: 2,
      title: 'Task Management App',
      description: 'A collaborative task management application with real-time updates, drag-and-drop functionality, and team collaboration features.',
      technologies: ['React', 'Firebase', 'Material-UI', 'Socket.io'],
      category: 'frontend',
      github: 'https://github.com/JaniduP2003/task-manager',
      demo: 'https://task-manager-demo.netlify.app',
      featured: true,
    },
    {
      id: 3,
      title: 'Weather Dashboard',
      description: 'A responsive weather application that provides current weather conditions and forecasts with beautiful visualizations.',
      technologies: ['React', 'OpenWeather API', 'Chart.js', 'CSS3'],
      category: 'frontend',
      github: 'https://github.com/JaniduP2003/weather-dashboard',
      demo: 'https://weather-dashboard-demo.vercel.app',
      featured: false,
    },
    {
      id: 4,
      title: 'REST API Server',
      description: 'A robust REST API server with authentication, data validation, and comprehensive documentation using Swagger.',
      technologies: ['Node.js', 'Express', 'PostgreSQL', 'JWT'],
      category: 'backend',
      github: 'https://github.com/JaniduP2003/rest-api-server',
      demo: null,
      featured: false,
    },
    {
      id: 5,
      title: 'Mobile Expense Tracker',
      description: 'A cross-platform mobile app for tracking expenses with categories, budgets, and spending analytics.',
      technologies: ['React Native', 'Expo', 'SQLite', 'Chart.js'],
      category: 'mobile',
      github: 'https://github.com/JaniduP2003/expense-tracker',
      demo: null,
      featured: true,
    },
    {
      id: 6,
      title: 'Portfolio Website',
      description: 'A responsive portfolio website built with React and Material-UI, showcasing projects and skills.',
      technologies: ['React', 'Material-UI', 'CSS3', 'Vercel'],
      category: 'frontend',
      github: 'https://github.com/JaniduP2003/portfolio',
      demo: 'https://janidu-portfolio.vercel.app',
      featured: false,
    },
  ];

  const filteredProjects = filter === 'all' 
    ? projects 
    : projects.filter(project => project.category === filter);

  // Contact data
  const contactInfo = [
    {
      icon: <Email />,
      title: 'Email',
      value: 'janidupeiris2003@gmail.com',
      link: 'mailto:janidupeiris2003@gmail.com',
    },
    {
      icon: <Phone />,
      title: 'Phone',
      value: '+94 77 123 4567',
      link: 'tel:+94771234567',
    },
    {
      icon: <LocationOn />,
      title: 'Location',
      value: 'Colombo, Sri Lanka',
      link: null,
    },
  ];

  const socialLinks = [
    {
      icon: <GitHub />,
      name: 'GitHub',
      url: 'https://github.com/JaniduP2003',
      color: '#333',
    },
    {
      icon: <LinkedIn />,
      name: 'LinkedIn',
      url: 'https://linkedin.com/in/janidu-peiris',
      color: '#0077b5',
    },
    {
      icon: <Twitter />,
      name: 'Twitter',
      url: 'https://twitter.com/janidu_peiris',
      color: '#1da1f2',
    },
  ];

  // Handler functions
  const handleFilterChange = (event, newFilter) => {
    if (newFilter !== null) {
      setFilter(newFilter);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.name || !formData.email || !formData.message) {
      setAlertMessage('Please fill in all required fields.');
      setShowAlert(true);
      return;
    }

    // Simulate form submission
    setTimeout(() => {
      setAlertMessage('Thank you for your message! I\'ll get back to you soon.');
      setShowAlert(true);
      setFormData({ name: '', email: '', subject: '', message: '' });
    }, 1000);
  };

  const handleCloseAlert = () => {
    setShowAlert(false);
  };

  return (
    <div className="full-page-theme">
      <Navbar scrollToSection={scrollToSection} />
      <section id="home">
        <main className="main-flex">
        <Container maxWidth="lg">
          <Box sx={{ textAlign: { xs: 'center', md: 'left' } }}>
            <Typography
              variant="h1"
              sx={{
