import React, { useState } from 'react';
import {
  Box,
  Typography,
  Container,
  Card,
  CardContent,
  TextField,
  Button,
  Grid,
  Paper,
  IconButton,
  Alert,
  CircularProgress,
} from '@mui/material';
import {
  Email,
  Phone,
  LocationOn,
  LinkedIn,
  GitHub,
  Twitter,
  Instagram,
  Send,
} from '@mui/icons-material';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitStatus('success');
      setFormData({ name: '', email: '', subject: '', message: '' });

      setTimeout(() => {
        setSubmitStatus('');
      }, 5000);
    }, 2000);
  };

  const contactInfo = [
    {
      icon: <Email />,
      title: 'Email',
      value: 'your.email@example.com',
      link: 'mailto:your.email@example.com'
    },
    {
      icon: <Phone />,
      title: 'Phone',
      value: '+1 (555) 123-4567',
      link: 'tel:+15551234567'
    },
    {
      icon: <LocationOn />,
      title: 'Location',
      value: 'Your City, Country',
      link: '#'
    },
    {
      icon: <LinkedIn />,
      title: 'LinkedIn',
      value: 'linkedin.com/in/yourprofile',
      link: 'https://linkedin.com/in/yourprofile'
    }
  ];

  const socialLinks = [
    { name: 'GitHub', icon: <GitHub />, url: 'https://github.com/yourusername', color: '#333' },
    { name: 'LinkedIn', icon: <LinkedIn />, url: 'https://linkedin.com/in/yourprofile', color: '#0077b5' },
    { name: 'Twitter', icon: <Twitter />, url: 'https://twitter.com/yourusername', color: '#1da1f2' },
    { name: 'Instagram', icon: <Instagram />, url: 'https://instagram.com/yourusername', color: '#e4405f' }
  ];

  return (
    <Container maxWidth="lg" sx={{ py: 8 }}>
      {/* Header */}
      <Box sx={{ textAlign: 'center', mb: 6 }}>
        <Typography
          variant="h2"
          sx={{
            mb: 2,
            background: 'linear-gradient(135deg, #ffffff, #4dabf7)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            position: 'relative',
            '&::after': {
              content: '""',
              position: 'absolute',
              bottom: -10,
              left: '50%',
              transform: 'translateX(-50%)',
              width: 60,
              height: 4,
              background: 'linear-gradient(135deg, #4dabf7, #f50057)',
              borderRadius: 2,
            },
          }}
        >
          Get In Touch
        </Typography>
        <Typography
          variant="h6"
          sx={{ color: 'text.secondary', maxWidth: 600, mx: 'auto', lineHeight: 1.6 }}
        >
          Let's connect and discuss opportunities, projects, or just have a chat!
        </Typography>
      </Box>

      <Grid container spacing={4}>
        {/* Left Column - Contact Info */}
        <Grid item xs={12} md={6}>
          {/* Intro Card */}
          <Card sx={{ mb: 3 }}>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h5" sx={{ fontWeight: 600, mb: 2 }}>
                Let's work together!
              </Typography>
              <Typography variant="body1" sx={{ color: 'text.secondary', lineHeight: 1.6 }}>
                I'm always interested in new opportunities, whether it's a 
                full-time position, internship, freelance project, or just 
                networking with fellow developers.
              </Typography>
            </CardContent>
          </Card>

          {/* Contact Methods */}
          <Card sx={{ mb: 3 }}>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 2 }}>
                Contact Information
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                {contactInfo.map((info, index) => (
                  <Paper
                    key={index}
                    component="a"
                    href={info.link}
                    target={info.link.startsWith('http') ? '_blank' : '_self'}
                    rel={info.link.startsWith('http') ? 'noopener noreferrer' : ''}
                    sx={{
                      p: 2,
                      display: 'flex',
                      alignItems: 'center',
                      gap: 2,
                      textDecoration: 'none',
                      color: 'inherit',
                      transition: 'all 0.3s ease',
                      backgroundColor: 'rgba(74, 171, 247, 0.05)',
                      border: '1px solid rgba(74, 171, 247, 0.2)',
                      '&:hover': {
                        transform: 'translateX(5px)',
                        borderColor: '#4dabf7',
                        backgroundColor: 'rgba(74, 171, 247, 0.1)',
                      },
                    }}
                  >
                    <Box sx={{ color: 'primary.main' }}>
                      {info.icon}
                    </Box>
                    <Box>
                      <Typography variant="body2" sx={{ color: 'text.secondary', fontSize: '0.9rem' }}>
                        {info.title}
                      </Typography>
                      <Typography variant="body1" sx={{ fontWeight: 500 }}>
                        {info.value}
                      </Typography>
                    </Box>
                  </Paper>
                ))}
              </Box>
            </CardContent>
          </Card>

          {/* Social Links */}
          <Card>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 2, textAlign: 'center' }}>
                Follow me on social media
              </Typography>
              <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2 }}>
                {socialLinks.map((social, index) => (
                  <IconButton
                    key={index}
                    component="a"
                    href={social.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    sx={{
                      width: 50,
                      height: 50,
                      backgroundColor: 'rgba(74, 171, 247, 0.1)',
                      border: '1px solid rgba(74, 171, 247, 0.3)',
                      color: 'primary.main',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-3px) scale(1.1)',
                        backgroundColor: 'primary.main',
                        color: 'white',
                        boxShadow: '0 8px 25px rgba(74, 171, 247, 0.3)',
                      },
                    }}
                  >
                    {social.icon}
                  </IconButton>
                ))}
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Right Column - Contact Form */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h5" sx={{ fontWeight: 600, mb: 3 }}>
                Send me a message
              </Typography>

              {submitStatus === 'success' && (
                <Alert severity="success" sx={{ mb: 3 }}>
                  Message sent successfully! I'll get back to you soon.
                </Alert>
              )}

              <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                <TextField
                  fullWidth
                  label="Name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  variant="outlined"
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(74, 171, 247, 0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: '#4dabf7',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#4dabf7',
                      },
                    },
                  }}
                />

                <TextField
                  fullWidth
                  label="Email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  variant="outlined"
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(74, 171, 247, 0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: '#4dabf7',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#4dabf7',
                      },
                    },
                  }}
                />

                <TextField
                  fullWidth
                  label="Subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleChange}
                  required
                  variant="outlined"
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(74, 171, 247, 0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: '#4dabf7',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#4dabf7',
                      },
                    },
                  }}
                />

                <TextField
                  fullWidth
                  label="Message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  required
                  multiline
                  rows={5}
                  variant="outlined"
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(74, 171, 247, 0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: '#4dabf7',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#4dabf7',
                      },
                    },
                  }}
                />

                <Button
                  type="submit"
                  variant="contained"
                  size="large"
                  disabled={isSubmitting}
                  startIcon={isSubmitting ? <CircularProgress size={20} /> : <Send />}
                  sx={{
                    py: 1.5,
                    fontSize: '1rem',
                    fontWeight: 600,
                  }}
                >
                  {isSubmitting ? 'Sending...' : 'Send Message'}
                </Button>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Contact;
